import React, { useState } from 'react';
import axios from 'axios';
import './ChatUI.css'; // Import the CSS

function ChatUI() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('https://your-api-endpoint.com/query', { query });
      setResponse(res.data.answer);
    } catch (error) {
      console.error('Error fetching answer:', error);
      setResponse('Something went wrong. Please try again.');
    }
  };

  return (
    <div className="chat-container">
      <h1 className="chat-title">What can I help you with?</h1>
      <form onSubmit={handleSubmit} className="chat-form">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Type your question..."
          required
          className="chat-input"
        />
        <button type="submit" className="chat-button">Ask</button>
      </form>
      <div className="chat-response">
        <strong>Answer:</strong>
        <p>{response}</p>
      </div>
    </div>
  );
}

export default ChatUI;
